function switchRotation(){var i=document.getElementById("rotationmode-toggle").checked,n=document.getElementsByClassName("rotationmode")[0],s=document.getElementById("180icon"),o=document.getElementById("90icon"),e=document.getElementsByClassName("Rot180"),t=document.getElementsByClassName("Rot90");if(i){if(console.log("Disabled 180s"),n.setAttribute("title","Turn on 180 rotations. Primarily affects percentages."),e.length!=0)for(let t=0;t<e.length;t++)e[t].style.display="none";if(t.length!=0)for(let e=0;e<t.length;e++)t[e].style.display="inline-block";s.style.opacity=1,o.style.opacity=0}else{if(console.log("Enabled 180s"),n.setAttribute("title","Turn off 180 rotations. Primarily affects percentages."),e.length!=0)for(let t=0;t<e.length;t++)e[t].style.display="inline-block";if(t.length!=0)for(let e=0;e<t.length;e++)t[e].style.display="none";s.style.opacity=0,o.style.opacity=1}}