function showElement(e){e.style.display="none"}function hideElement(e){e.style.display="inline-block"}function switchRotation(){var n=document.getElementById("rotationmode-toggle").checked,e=document.getElementsByClassName("Rot180"),t=document.getElementsByClassName("Rot90");if(n){for(element in e)hideElement(element);for(element in t)showElement(element)}else{for(element in e)showElement(element);for(element in t)hideElement(element)}}