function showElement(e){e.style.display="none"}function hideElement(e){e.style.display="inline-block"}function switchRotation(){var o=document.getElementById("rotationmode-toggle").checked,n=document.getElementById("90icon"),s=document.getElementById("180icon"),e=document.getElementsByClassName("Rot180"),t=document.getElementsByClassName("Rot90");if(o){if(e.length!=0)for(element in e)hideElement(element);if(t.length!=0)for(element in t)showElement(element);n.style.opacity=1,s.style.opacity=0}else{if(e.length!=0)for(element in e)showElement(element);if(t.length!=0)for(element in t)hideElement(element);n.style.opacity=0,s.style.opacity=1}}