function switchRotation(){var o=document.getElementById("rotationmode-toggle").checked,n=document.getElementById("90icon"),s=document.getElementById("180icon"),e=document.getElementsByClassName("Rot180"),t=document.getElementsByClassName("Rot90");if(o){if(console.log("Disabled 180s"),e.length!=0)for(let t=0;t<e.length;t++)e[t].style.display="none";if(t.length!=0)for(let e=0;e<t.length;e++)t[e].style.display="inline-block";n.style.opacity=1,s.style.opacity=0}else{if(console.log("Enabled 180s"),e.length!=0)for(let t=0;t<e.length;t++)e[t].style.display="inline-block";if(t.length!=0)for(let e=0;e<t.length;e++)t[e].style.display="none";n.style.opacity=0,s.style.opacity=1}}